import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    user: null,
    token: null,
  },
  mutations: {
    setUser(state, user) {
      state.user = user;
    },
    setToken(state, token) {
      state.token = token;
    },
  },
  actions: {
    setUser (context) {
      context.commit('setUser');
    },
    setToke (context) {
      context.commit('setUser');
    },
  },
  getters: {
    isLoggedIn(state) {
      return !!state.token;
    },
  },

});
