import axios from "axios"; // import console from "console";

import { mapMutations } from "vuex";
export default {
  name: "LoginSection",
  data: () => {
    return {
      username: "",
      password: "",
      error: ""
    };
  },
  methods: { ...mapMutations(["setUser", "setToken"]),

    async login() {
      console.log(this.username, this.password);

      try {
        const response = await axios.post("http://localhost:4000/users/authenticate", {
          username: this.username,
          password: this.password
        });
        const {
          username,
          token
        } = await response.data;
        console.log(response);
        this.setUser(username);
        this.setToken(token);
        this.$router.push("/");
      } catch (error) {
        console.error(error);
      }
    }

  }
};
