import axios from "axios"; // import console from "console";

import { mapActions } from "vuex"; // this.$store.getters

export default {
  name: "LoginSection",
  data: () => {
    return {
      loginForm: {
        username: "",
        password: ""
      }
    };
  },
  methods: { ...mapActions(["setUser", "setToken"]),

    async login() {
      // console.log(this.username, this.password);
      try {
        const response = await axios.post("http://localhost:4000/users/authenticate", {
          username: this.username,
          password: this.password
        });
        const {
          username,
          token
        } = await response.data; // console.log(username, token);

        this.setUser(username);
        this.setToken(token);
        this.$router.push("/home");
      } catch (error) {
        console.error(error);
      }
    }

  },
  computed: {}
};
