import axios from "axios";
import { mapMutations } from "vuex";
export default {
  name: "LoginSection",
  data: () => {
    return {
      username: "",
      password: "",
      error: ""
    };
  },
  methods: { ...mapMutations(["setUser", "setToken"]),

    async login() {
      console.log(this.username, this.password);

      try {
        const response = await axios("http://localhost:4000/users/authenticate", {
          method: "POST",
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify({
            username: this.username,
            password: this.password
          })
        });
        const {
          user,
          token
        } = await response.json();
        this.setUser(user);
        this.setToken(token);
        this.$router.push("/");
      } catch (error) {
        console.error(error);
      }
    }

  }
};
