import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

const store = new Vuex.Store({
  state: {
    user: null,
    token: null,
  },
  //manipulating the str
  mutations: {
    setUser(state, user) {
      state.user = user;
    },
    setToken(state, token) {
      state.token = token;
    },
  },
  //commiting the 
  actions: {
    setUser() {
      store.commt("setUser");
    },
    setToken() {
      store.commt("setToken");
    },
  },

  //compute all derived state -when there is a change in state
  getters: {
    isLoggedIn(state) {
      return !!state.token;
    },
    user(state) {
      return state.user;
    },
    token(state) {
      return state.token;
    },
  },
});

export default store;
